function readCookie(e){for(var n=encodeURIComponent(e)+"=",o=document.cookie.split(";"),t=0;t<o.length;t++){for(var i=o[t];" "===i.charAt(0);)i=i.substring(1,i.length);if(0===i.indexOf(n))return decodeURIComponent(i.substring(n.length,i.length))}return null}function createCookie(e,n,o){var t;if(o){var i=new Date;i.setTime(i.getTime()+24*o*60*60*1e3),t="; expires="+i.toGMTString()}else t="";document.cookie=encodeURIComponent(e)+"="+encodeURIComponent(n)+t+"; path=/"}function hideNewsIndicator(){createCookie("newsLastRead",Date.now()),$(".news-indicator").css({display:"none"})}